mapper.update:

  		/* common.mapUpdate */
	    UPDATE SET TB_KKAMAP
	         	   MOIM_LATI = #{MOIM_LATI}
	         	   MOIM_LONG = #{MOIM_LONG}
	         	   MOIM_ADR1 = #{MOIM_ADR1}
	         	   MOIM_ADR2 = #{MOIM_ADR2}
	         WHERE MOIM_IDXX = #{MOIM_IDXX}

mapper.insert:


	SELECT LPAD(NVL(MAX(TO_NUMBER(HASH_SEQC)), 0) + 1, 3, '0') AS HASH_SEQC  
	  FROM TB_HASHTG
	 WHERE HASH_IDXX = #{MOIM_IDXX} /* 해시태그 시퀀스 순차채번 */



  		/* common.tagInsert */
	   INSERT INTO TB_HASHTG (
	   			   HASH_IDXX
	   			 , HASH_SEQC
	   			 , HASH_TAGG) 
	   	  VALUES (
	   	  		   #{MOIM_IDXX}
	   	  		 , #{HASH_SEQC}
	   	  		 , #{HASH_TAGG}
	   	  		 )

mapper.delete:

  		/* common.tagInsert */
	  	DELETE FROM TB_HASHTG
	  	      WHERE HASH_IDXX = #{MOIM_IDXX}

mapper.insert:

/* common.likdeInsert */
INSERT INTO TM_HEARTS (
		      LIKE_IDXX
		    , USER_NUMB
 ) VALUES (
             #{LIKE_IDXX}
           , #{USER_NUMB}
           )

mapper.delete:

/* common.likeDelete */
DELETE TM_HEARTS 
 WHERE LIKE_IDXX = #{LIKE_IDXX}  
   AND USER_NUMB = #{USER_NUMB}

mapper.insert:

/* common.follow */
INSERT INTO TB_FOLLOW (
		      USER_NUMB
		    , FOLW_USER
 ) VALUES (
             #{USER_NUMB}
           , #{FOLW_USER}
           )

mapper.delete:

/* common.unfollow */
DELETE TB_FOLLOW 
 WHERE USER_NUMB = #{USER_NUMB}  
   AND FOLW_USER = #{FOLW_USER}
