mapper.insert:

 	 /* detail.moimJoin */
    INSERT INTO TB_MEMBER (
           MOIM_IDXX
         , USER_NUMB
         , BANN_YSNO
         , WAIT_YSNO
         , PART_DATE
) VALUES (
           #{MOIM_IDXX}
         , #{USER_NUMB}
         , 'N'
         , #{WAIT_YSNO}
         , SYSDATE
         )

mapper.update:

	 /* detail.moimStateUpdate */
   UPDATE TB_MEMBER 
      SET BANN_YSNO = #{BANN_YSNO}
        , WAIT_YSNO = #{WAIT_YSNO}
    WHERE MOIM_IDXX = #{MOIM_IDXX}
      AND USER_NUMB = #{USER_NUMB}
