mapper.select:
SELECT COUNT(*) AS MO_COUNT FROM MOIM WHERE MO_DEL_YN = 'N' AND (CASE WHEN TO_CHAR(MO_DEADLINE,'YYMMDD')+'3' <= TO_CHAR(SYSDATE,'YYMMDD') AND MO_CLOSE_YN = 'Y' THEN 'Y' ELSE 'N' END) = 'N'
AND MO_CATEGORY = #{MO_CATEGORY}
AND MO_REGION = #{MO_REGION}
AND MO_TITLE LIKE '%'||#{KEYWORD}||'%'
AND MO_DETAILCATEGORY LIKE '%'||#{KEYWORD2}||'%'


SELECT COUNT(*) AS CHAMYN
FROM MOIM M, MEMBER MEM, PARTYMEM P
WHERE M.MO_IDX = P.MO_IDX
AND P.M_IDX = #{M_IDX}
AND M.MO_IDX = #{MO_IDX}


SELECT P.P_FINAL_YN, P.P_BAN_YN
FROM MOIM M, MEMBER MEM, PARTYMEM P
WHERE M.MO_IDX = P.MO_IDX
AND MEM.M_IDX = P.M_IDX
AND P.M_IDX = #{M_IDX}
AND M.MO_IDX = #{MO_IDX}

mapper.update:

UPDATE PARTYMEM
SET 
P_BAN_YN = 'N',
P_FINAL_YN = 'Y'
WHERE M_IDX = #{M_IDX}
AND MO_IDX = #{MO_IDX}


UPDATE PARTYMEM
SET 
P_BAN_YN = 'Y',
P_FINAL_YN = 'N'
WHERE M_IDX = #{M_IDX}
AND MO_IDX = #{MO_IDX}

mapper.select:
    SELECT COUNT(RV_IDX)  AS RV_YN
	FROM REVIEW
	WHERE MO_IDX = #{MO_IDX}
	AND M_IDX = #{M_IDX}

SELECT RV_IDX, M_IDX
FROM REVIEW
WHERE M_IDX = #{M_IDX}
AND MO_IDX = #{MO_IDX}

mapper.update:

UPDATE MOIM SET
WII= #{WII},
KYUNG = #{KYUNG},
ADDRESS= #{ADDRESS}, 
DEADDRESS = #{DEADDRESS, jdbcType=VARCHAR}
WHERE MO_IDX = #{MO_IDX}


UPDATE MOIM SET
MO_CLOSE_YN = 'Y'
WHERE MO_IDX = #{MO_IDX}


UPDATE MOIM SET
MO_DEL_YN = 'Y'
WHERE MO_IDX = #{MO_IDX}
