mapper.select:

      	 /*join.inertCate*/
         SELECT COUNT(1)
		   FROM TB_URCATE
		  WHERE USER_NUMB = #{USER_NUMB}
  			AND CATE_IDXX = #{CATE_IDXX}


    	 /*join.updateNewstCate*/
      UPDATE TB_URCATE
         SET REGG_DATE = SYSDATE
       WHERE USER_NUMB = #{USER_NUMB}
		   AND CATE_IDXX = #{CATE_IDXX}

mapper.insert:

	 /*join.inertCate*/
   INSERT INTO TB_URCATE(
              USER_NUMB
            , CATE_IDXX
            , REGG_DATE
   ) VALUES (
             #{USER_NUMB}
           , #{CATE_IDXX}
           , SYSDATE
             )
