mapper.select:
SELECT /*mainPage.getGather*/ * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY A.REGG_DATE, A.MOIM_TIME, A.MOIM_IDXX DESC) RNUM , A.MOIM_IDXX /* 게더아이디 */ , A.MOIM_TITL /* 게더 제목 */ , A.MOIM_CNTT /* 게더 내용 */ , B.MOIM_LATI /* 위도 */ , B.MOIM_LONG /* 경도 */ , B.MOIM_ADR1 /* 주소1 */ , B.MOIM_ADR2 /* 주소2 */ , A.MOIM_IMAG /* 게더 대표 이미지 */ , A.CATE_IDXX /* 카테고리 코드 */ , A.PARENTS_CATE /* 부모 카테고리 */ , A.CHILD_CATE /* 자식 카테고리 */ , A.MAXX_PEOP /* 최대 인원 */ , A.PRES_PEOP /* 현재 인원 n/n */ , A.MEMB_COUNT /* 현재 인원수 */ , A.MOIM_COST /* 참가 비용 */ , A.APPR_YSNO /* 승인 여부 */ , A.MINN_AGEE /* 최소나이 */ , A.MAXX_AGEE /* 최대나이 */ , A.APPR_LMIT /* 모임 제한 KR */ , A.GNDR_CODE /* 모임 성별 코드 */ , A.PREGI_NAME /* 부모 지역명 */ , A.REGI_NAME /* 자식 지역명 */ , A.USER_NUMB /* 방장 회원번호 */ , A.USER_NICK /* 방장 닉네임 */ , A.USER_IMAG /* 방장 프로필 사진 */ , A.SELF_INTR /* 방장 자기소개 */ , A.SMAL_DATE /* 날짜 형식 MM.dd(day) 오전/오후 HH:mm */ , A.FULL_DATE /* 날짜 형식 YYYY년 M월 오전/오후 HH:mm */ , A.LIKE_COUNT /* 좋아요 수 */ , A.REGG_DATE /* 모임 개설일 */ , A.ENDD_YSNO /* 모임 마감 유무 */ , getAmILike(MOIM_IDXX, #{USER_NUMB, jdbcType=VARCHAR}) AS LIKE_YSNO /* 로그인유저 좋아요 유무 */ , getAmIBkmk(MOIM_IDXX, #{USER_NUMB, jdbcType=VARCHAR}) AS BKMK_YSNO /* 로그인유저 북마크 유무 */ <![CDATA[ FROM VW_MOIMXM A LEFT JOIN TB_KKAMAP B ON A.MOIM_IDXX = B.MOIM_IDXX WHERE 1=1
AND A.MOIM_IDXX = #{MOIM_IDXX}
AND A.CATE_IDXX LIKE '%'||#{CATE_IDXX}||'%'
AND A.CATE_IDXX IN
#{WEATH_CATE}
AND A.REGI_CODE LIKE '%'||#{CITY_CODE}||'%'
AND (A.REGI_NAME LIKE '%'||#{KEYY_WORD}||'%') OR (A.PREGI_NAME LIKE '%'||#{KEYY_WORD}||'%') OR (A.CHILD_CATE LIKE '%'||#{KEYY_WORD}||'%') OR (A.PARENTS_CATE LIKE '%'||#{KEYY_WORD}||'%') OR (A.MOIM_TITL LIKE '%'||#{KEYY_WORD}||'%') OR (A.MOIM_CNTT LIKE '%'||#{KEYY_WORD}||'%') OR (A.USER_NICK LIKE '%'||#{KEYY_WORD}||'%') OR (A.MOIM_IDXX IN (SELECT HASH_IDXX FROM TB_HASHTG WHERE HASH_TAGG LIKE '%'||#{KEYY_WORD}||'%') )
)
WHERE ROWNUM <= 12

SELECT /*gather.getGahterMember*/ A.MOIM_IDXX /* 모임번호 */ , CASE WHEN A.USER_NUMB = C.USER_NUMB THEN 'Y' ELSE 'N' END AS MAST_YSNO /* 방장유무 */ , A.USER_NUMB /* 회원번호 */ , B.USER_NICK /* 회원닉네임 */ , B.SELF_INTR /* 회원자기소개 */ , getuserimage(B.FILE_SVNM, B.USER_NUMB) AS USER_IMAG /* 회원프사 */ , A.BANN_YSNO /* 강퇴유무 */ , A.WAIT_YSNO /* 대기유무 */ , A.PART_DATE
, CASE WHEN A.BANN_YSNO = 'N' AND A.WAIT_YSNO = 'N' AND C.ENDD_YSNO = 'Y' AND D.REVW_IDXX IS NULL THEN 'Y' ELSE 'N' END AS REVW_YSNO /* 리뷰자격 */
FROM TB_MEMBER A LEFT JOIN TM_USERXM B ON A.USER_NUMB = B.USER_NUMB LEFT JOIN TM_MOIMXM C ON A.MOIM_IDXX = C.MOIM_IDXX LEFT JOIN TB_REVIEW D ON A.MOIM_IDXX = D.MOIM_IDXX WHERE A.MOIM_IDXX = #{MOIM_IDXX}
AND A.USER_NUMB = #{USER_NUMB}
ORDER BY A.PART_DATE

mapper.update:

      UPDATE TM_GATHER SET
