		     REGI_CODE = #{REGI_CODE}
		     APPR_YSNO = #{APPR_YSNO}
		     APPR_GNDR = #{APPR_GNDR}
		     MINN_AGEE = #{MINN_AGEE}
		     MINN_PEOP = #{MINN_PEOP}
		     MOIM_COST = #{MOIM_COST}
		     MOIM_DATE = #{MOIM_DATE}
		     MOIM_DATE = #{MOIM_DATE}
		     MOIM_TIME = #{MOIM_TIME}
	   WHERE MOIM_IDXX = #{MOIM_IDXX}
